@bot.message_handler(commands=['start'])
def start(message):
    bot.send_message(message.chat.id, f'*–ü—Ä–∏–≤–µ—Ç—Å—Ç–≤—É–µ–º –í–∞—Å, {message.from_user.first_name} , –≤ –Ω–∞—à–µ–º —Ç–µ–ª–µ–≥—Ä–∞–º–º-–±–æ—Ç–µ ‚Äú–ò–¥–µ–∏ –ü–æ–¥–∞—Ä–∫–æ–≤ üéÅ‚Äù!* \n\n–ú—ã –∑–¥–µ—Å—å, —á—Ç–æ–±—ã –ø–æ–º–æ—á—å –≤–∞–º –Ω–∞–π—Ç–∏ –∏–¥–µ–∞–ª—å–Ω—ã–π –ø–æ–¥–∞—Ä–æ–∫ –¥–ª—è –ª—é–±–æ–≥–æ –æ—Å–æ–±–µ–Ω–Ω–æ–≥–æ —á–µ–ª–æ–≤–µ–∫–∞ –≤ –≤–∞—à–µ–π –∂–∏–∑–Ω–∏. –° –Ω–∞–º–∏ –∏–¥–µ–∏ –ø–æ–¥–∞—Ä–∫–æ–≤ –±–æ–ª—å—à–µ –Ω–µ –±—É–¥—É—Ç –ø—Ä–æ–±–ª–µ–º–æ–π! \n\n–ö–∞–∫ —ç—Ç–æ —Ä–∞–±–æ—Ç–∞–µ—Ç?\n–ü—Ä–æ—Å—Ç–æ –≤–≤–µ–¥–∏—Ç–µ –ø–æ–ª —á–µ–ª–æ–≤–µ–∫–∞, –¥–ª—è –∫–æ—Ç–æ—Ä–æ–≥–æ –í—ã –∏—â–µ—Ç–µ –ø–æ–¥–∞—Ä–æ–∫, –∏ —Å–æ–±—ã—Ç–∏–µ. –ù–∞—à –±–æ—Ç –ø–æ–¥–±–µ—Ä–µ—Ç –¥–ª—è –í–∞—Å –Ω–µ—Å–∫–æ–ª—å–∫–æ –∏–¥–µ–π. –í–∞–º –æ—Å—Ç–∞–Ω–µ—Ç—Å—è —Ç–æ–ª—å–∫–æ –≤—ã–±—Ä–∞—Ç—å —Ç—É, –∫–æ—Ç–æ—Ä–∞—è –±–æ–ª—å—à–µ –≤—Å–µ–≥–æ –ø–æ–Ω—Ä–∞–≤–∏—Ç—Å—è! ‚ò∫Ô∏è \n\n–ù–µ –∑–Ω–∞–µ—Ç–µ, —á—Ç–æ –ø–æ–¥–∞—Ä–∏—Ç—å –¥—Ä—É–≥—É, –∫–æ–ª–ª–µ–≥–µ, –ª—é–±–∏–º–æ–º—É —á–µ–ª–æ–≤–µ–∫—É –∏–ª–∏ –¥–∞–∂–µ —Å–∞–º–æ–º—É —Å–µ–±–µ?\n–ú—ã –∑–¥–µ—Å—å, —á—Ç–æ–±—ã —Å–¥–µ–ª–∞—Ç—å –í–∞—à—É –∂–∏–∑–Ω—å –ø—Ä–æ—â–µ –∏ —Ä–∞–¥–æ—Å—Ç–Ω–µ–µ! –î–∞–≤–∞–π—Ç–µ –≤–º–µ—Å—Ç–µ —Å–æ–∑–¥–∞–≤–∞—Ç—å –Ω–µ–∑–∞–±—ã–≤–∞–µ–º—ã–µ –º–æ–º–µ–Ω—Ç—ã —Å –Ω–∞—à–∏–º–∏ –ø–æ–¥–∞—Ä–∫–∞–º–∏! üöÄ', parse_mode='Markdown')


@bot.message_handler(commands=['begin'])
def begin(message):
    markup = types.InlineKeyboardMarkup()
    a = types.InlineKeyboardButton('–î–µ–Ω—å –†–æ–∂–¥–µ–Ω–∏—è', callback_data='HB')
    b = types.InlineKeyboardButton('–ù–æ–≤—ã–π –≥–æ–¥', callback_data='NY')
    c = types.InlineKeyboardButton('23 –§–µ–≤—Ä–∞–ª—è', callback_data='Feb23')
    d = types.InlineKeyboardButton('8 –ú–∞—Ä—Ç–∞', callback_data='Mar8')
    markup.row(a, b)
    markup.row(c,d)
    bot.send_message(message.chat.id, '–í—ã–±–µ—Ä–∏—Ç–µ –ø–æ–¥—Ö–æ–¥—è—â–µ–µ —Å–æ–±—ã—Ç–∏–µ:', reply_markup=markup)


